import React from "react";

class App extends React.Component{
    constructor(){
        super();
        this.state = {
            testParam: "000"
        }
    }

    componentDidMount(){
        setTimeout(() => {
            this.setState({
                testParam: "1"
            })
        }, 1000);
    }

    render(){
        return (
            <Child testParam={this.state.testParam}/>
        )
    }
}

class Child extends React.Component{
    // componentDidMount(){
