import React from "react";
import { render } from "react-dom";

class App extends React.Component{
    constructor(){
        super(this);
        this.state = {
            testParam: []
        }
    }

    componentDidMount(){
        setTimeout(() => {
            this.setState({
                testParam: [1,2]
            })
        }, 1000);
    }

    render(){
        return (
            <Child testParam={this.state.testParam}/>
        )
    }
}

class Child extends React.Component{
    componentDidMount(){
        console.log(this.props.testParam)
    }
}

export default App
