import React from 'react';
import ReactDOM from 'react-dom';
import {createStore} from 'redux';
import {
    addAction,
    squareAction
  }from './actions/actions'
import math from './reducers/math'

function App(){

  const store = createStore(math);

  console.log(store.getState());//每一次都会进入reducer

  console.log(store.dispatch(addAction(1)))//传进去action

  console.log(store.getState())

  console.log(store.dispatch(addAction(2)))

  console.log(store.getState())

  console.log(store.dispatch(squareAction()))

  console.log(store.getState())

  return (
    <div>
      <p>{store.getState()}</p>
      <button onClick={() => store.dispatch(addAction(1))}>+1</button>
      <button onClick={() => store.dispatch(addAction(2))}>+2</button>
      <button onClick={() => store.dispatch(addAction(1))}>**</button>
    </div>
  )
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);


